   101â†’    // anchor æ˜¯ç‰©ç†åæ ‡ï¼Œwidth/height æ˜¯é€»è¾‘å°ºå¯¸ï¼ˆå› ä¸º CSS åƒç´ ï¼‰
   102â†’    // ä½†æˆ‘ä»¬éœ€è¦è®¡ç®—æœ€ç»ˆçš„ç‰©ç†åæ ‡ä¼ ç»™ Tauri
   103â†’    // Tauri çš„ setSize(LogicalSize) ä¼šè‡ªåŠ¨å¤„ç†ç¼©æ”¾ï¼Œä½† setPosition(PhysicalPosition) ä¸ä¼š
   104â†’    // æ‰€ä»¥è¿™é‡Œæˆ‘ä»¬éœ€è¦å°å¿ƒï¼šå¦‚æœ anchor æ˜¯ç‰©ç†åæ ‡ï¼Œé‚£ä¹ˆè®¡ç®—åç§»é‡æ—¶ä¹Ÿè¦è€ƒè™‘ç¼©æ”¾å—ï¼Ÿ
   105â†’    // ä¸ºäº†ç®€å•ä¸”å‡†ç¡®ï¼Œæˆ‘ä»¬å‡è®¾ä¼ å…¥çš„ width/height å·²ç»åŒ…å« DPI ç¼©æ”¾ï¼ˆæˆ–è€…æˆ‘ä»¬åœ¨è¿™é‡Œè·å– DPRï¼‰
   106â†’    
   107â†’    const dpr = window.devicePixelRatio || 1;
   108â†’    // å°†é€»è¾‘å°ºå¯¸è½¬ä¸ºç‰©ç†å°ºå¯¸è¿›è¡Œè®¡ç®—
   109â†’    const pWidth = width * dpr;
   110â†’    const pHeight = height * dpr;
   111â†’
   112â†’    // å±å¹•ç‰©ç†åˆ†è¾¨ç‡ï¼ˆæ³¨æ„ï¼šwindow.screen.width è¿”å›çš„æ˜¯é€»è¾‘åˆ†è¾¨ç‡ï¼‰
   113â†’    // å¦‚æœè¦è·å–çœŸå®çš„ç‰©ç†åˆ†è¾¨ç‡ï¼Œåº”è¯¥ç”¨ screen.width * dpr
   114â†’    const screenWidth = window.screen.width * dpr;
   115â†’    const screenHeight = window.screen.height * dpr;
   116â†’
   117â†’    const offset = 16 * dpr;
   118â†’    const safe = 8 * dpr;
   119â†’
   120â†’    // å¦‚æœæ²¡æœ‰ anchorï¼Œå±…ä¸­
   121â†’    const base = anchor ?? { x: screenWidth / 2, y: safe };
   122â†’    
   123â†’    let posX = Math.round(base.x) + offset;
   124â†’    let posY = Math.round(base.y) + offset;
   125â†’
   126â†’    // è¾¹ç•Œæ£€æµ‹ï¼ˆå…¨éƒ¨åœ¨ç‰©ç†åƒç´ ç©ºé—´è¿›è¡Œï¼‰
   127â†’    if (posX + pWidth > screenWidth - safe) {
   128â†’      posX = Math.round(base.x) - pWidth - offset;
   129â†’    }
   130â†’    if (posY + pHeight > screenHeight - safe) {
   131â†’      posY = Math.round(base.y) - pHeight - offset;
   132â†’    }
   133â†’
   134â†’    // æœ€åçš„å®‰å…¨è¾¹ç•Œé™åˆ¶
   135â†’    posX = Math.max(safe, Math.min(posX, screenWidth - pWidth - safe));
   136â†’    posY = Math.max(safe, Math.min(posY, screenHeight - pHeight - safe));
   137â†’
   138â†’    log("compute-position", {
   139â†’      anchor: base,
   140â†’      width: pWidth,
   141â†’      height: pHeight,
   142â†’      screen: { w: screenWidth, h: screenHeight },
   143â†’      result: { posX, posY },
   144â†’    });
   145â†’
   146â†’    return { posX, posY };
   147â†’  };
   148â†’
   149â†’  useEffect(() => {
   150â†’    const unlisten = listen<TranslateEvent>("translate-text", async (event) => {
   151â†’      const seq = (requestSeq.current += 1);
   152â†’      clearHideTimer();
   153â†’
   154â†’      // å¦‚æœçª—å£å·²ç»æ˜¾ç¤ºï¼Œå…ˆéšè—å®ƒä»¥ç¡®ä¿å”¯ä¸€æ€§
   155â†’      if (view !== null) {
   156â†’        const win = getCurrentWindow();
   157â†’        await win.hide();
   158â†’        setView(null);
   159â†’        log("translate-text", "hiding existing window for new translation");
   160â†’      }
   161â†’
   162â†’      const { text, x, y } = event.payload;
   163â†’      // x, y æ˜¯åç«¯ä¼ æ¥çš„ç‰©ç†åæ ‡ï¼Œç›´æ¥å­˜å‚¨
   164â†’      lastAnchor.current = { x, y };
   165â†’      log("translate-text", { seq, length: text.length, x, y });
   166â†’
   167â†’      try {
   168â†’        // å…ˆè°ƒç”¨ç¿»è¯‘ APIï¼Œä¸æ˜¾ç¤ºçª—å£
   169â†’        log("translate-api", "calling translate API");
   170â†’        const res = await invoke<TranslateResult>("translate", { text });
   171â†’        if (seq !== requestSeq.current) {
   172â†’          log("translate-api", "sequence mismatch, ignoring result");
   173â†’          return;
   174â†’        }
   175â†’
   176â†’        // æ£€æŸ¥ç¿»è¯‘æ˜¯å¦æˆåŠŸä¸”æœ‰ç»“æœ
   177â†’        if (!res.success || !res.text || res.text.trim() === "") {
   178â†’          log("translate-api", "no valid result, not showing window", { success: res.success, hasText: !!res.text, error: res.error });
   179â†’          return;
   180â†’        }
   181â†’
   182â†’        log("translate-api", "translation successful, showing window");
   183â†’
   184â†’        // ç¿»è¯‘æˆåŠŸï¼Œå‡†å¤‡æ˜¾ç¤ºçª—å£
   185â†’        setView({ status: "done", result: res });
   186â†’
   187â†’        const win = getCurrentWindow();
   188â†’        const width = getPopupWidth(res.text.length);
   189â†’        setPopupWidth(width);
   190â†’
   191â†’        // æ·»åŠ  padding ç©ºé—´ä»¥æ˜¾ç¤ºé˜´å½±å’Œåœ†è§’
   192â†’        const padding = 16; // 8px * 2
   193â†’        const initialHeight = 140; // å®¹çº³å¤´éƒ¨ + ä¸¤è¡Œå†…å®¹
   194â†’        await win.setSize(new LogicalSize(width + padding, initialHeight + padding));
   195â†’
   196â†’        // è®¡ç®—ç‰©ç†åæ ‡ä½ç½®
   197â†’        const { posX, posY } = computePosition({ anchor: { x, y }, width: width + padding, height: initialHeight + padding });
   198â†’        await win.setPosition(new PhysicalPosition(posX, posY));
   199â†’
   200â†’        await win.show();
   201â†’        await win.setFocus();
   202â†’        setIsFirstShow(false); // çª—å£æ˜¾ç¤ºåï¼Œç¦ç”¨åç»­åŠ¨ç”»
   203â†’        log("window-show", { posX, posY, resultLength: res.text.length });
   204â†’
   205â†’        log("translate-result", { seq, success: res.success, length: res.text.length });
   206â†’      } catch (e) {
   207â†’        if (seq !== requestSeq.current) return;
   208â†’        log("translate-error", { seq, error: String(e) });
   209â†’        // ç¿»è¯‘å¤±è´¥ï¼Œä¸æ˜¾ç¤ºçª—å£
   210â†’      }
   211â†’    });
   212â†’
   213â†’    return () => {
   214â†’      unlisten.then((f) => f());
