useLayoutEffect(() => {
  // åªåœ¨ç¿»è¯‘å®Œæˆåæ‰è°ƒæ•´çª—å£é«˜åº¦ï¼Œé¿å… loading çŠ¶æ€æ—¶çš„å¸ƒå±€è·³åŠ¨
  if (!view || view.status !== "done" || !contentRef.current) return;
  const win = getCurrentWindow();

  const raf = window.requestAnimationFrame(async () => {
    if (!contentRef.current) return;

    // æ ¹æ®ç¿»è¯‘ç»“æœé•¿åº¦é‡æ–°è®¡ç®—å®½åº¦
    const resultLength = view.result.success ? view.result.text.length : 0;
    const width = getPopupWidth(resultLength);
    setPopupWidth(width); // æ›´æ–°çŠ¶æ€ä»¥ä¾¿ä¸‹æ¬¡ä½¿ç”¨

    // ä½¿ç”¨ scrollHeight ä»¥è·å–å®Œæ•´å†…å®¹é«˜åº¦ï¼Œé˜²æ­¢ overflow hidden å¯¼è‡´æµ‹é‡ä¸å‡†
    const contentHeight = contentRef.current.scrollHeight;
    // ç§»é™¤æœ€å°é«˜åº¦é™åˆ¶ï¼Œè®©çª—å£æ ¹æ®å®é™…å†…å®¹è‡ªé€‚åº”
    const minHeight = 80; // åªä¿è¯åŸºæœ¬çš„æœ€å°é«˜åº¦ï¼ˆå¤´éƒ¨ + å°‘é‡å†…å®¹ï¼‰
    // å¢åŠ æœ€å¤§é«˜åº¦åˆ°å±å¹•é«˜åº¦çš„ 85%ï¼Œå‡å°‘æ»šåŠ¨æ¡å‡ºç°
    const maxHeight = Math.floor(window.screen.height * 0.85);
    const height = Math.min(Math.max(contentHeight, minHeight), maxHeight);

    // æ·»åŠ  padding ç©ºé—´ä»¥æ˜¾ç¤ºé˜´å½±å’Œåœ†è§’
    const padding = 16; // 8px * 2
    await win.setSize(new LogicalSize(width + padding, height + padding));

    // ä½¿ç”¨æœ€æ–°çš„ç‰©ç†å°ºå¯¸è®¡ç®—ä½ç½®
    const { posX, posY } = computePosition({ anchor: lastAnchor.current, width: width + padding, height: height + padding });
    await win.setPosition(new PhysicalPosition(posX, posY));

    log("window-layout", { width, height, contentHeight, posX, posY, status: view.status, resultLength });

    // æ£€æŸ¥é¼ æ ‡æ˜¯å¦åœ¨çª—å£å†…ï¼Œå¦‚æœä¸åœ¨åˆ™å¯åŠ¨è‡ªåŠ¨éšè—å®šæ—¶å™¨
    // ä½¿ç”¨ :hover ä¼ªç±»æ£€æŸ¥
    const isMouseOver = contentRef.current.matches(':hover');
    log("auto-hide-check", { isMouseOver, hasTimer: !!hideTimer.current });

    if (!isMouseOver && !hideTimer.current) {
      log("auto-hide-check", "mouse not over window, starting timer");
      hideTimer.current = window.setTimeout(() => {
        log("hideTimer", "auto-hide timer elapsed, calling hideWindow");
        hideWindow();
      }, autoCloseTimeout);
    }
  });

  return () => window.cancelAnimationFrame(raf);
}, [view]);

