function App() {
  const [view, setView] = useState<ViewState | null>(null);
  const [popupWidth, setPopupWidth] = useState(300);
  const [isFirstShow, setIsFirstShow] = useState(true);
  const [autoCloseTimeout, setAutoCloseTimeout] = useState(1500);
