        "Y" => Ok(Code::KeyY), "Z" => Ok(Code::KeyZ),
        "0" => Ok(Code::Digit0), "1" => Ok(Code::Digit1), "2" => Ok(Code::Digit2), "3" => Ok(Code::Digit3),
        "4" => Ok(Code::Digit4), "5" => Ok(Code::Digit5), "6" => Ok(Code::Digit6), "7" => Ok(Code::Digit7),
        "8" => Ok(Code::Digit8), "9" => Ok(Code::Digit9),
