    let url = format!("https://api.deeplx.org/{}/translate", api_key);

    match client
        .post(&url)
        .json(&req)
        .timeout(Duration::from_secs(10))
        .send()
        .await
    {
        Ok(resp) => {
            let status = resp.status();
            if status.is_success() {
