        _ => Err(format!("ä¸æ”¯æŒçš„æŒ‰é”®: {}", key))
    }
}

fn parse_shortcut(shortcut_str: &str) -> Result<Shortcut, String> {
    let parts: Vec<&str> = shortcut_str.split('+').map(|s| s.trim()).collect();
    if parts.is_empty() {
        return Err("å¿«æ·é”®ä¸èƒ½ä¸ºç©º".to_string());
    }

    let mut modifiers = Modifiers::empty();
    let mut key_code = None;

    for part in parts {
        match part {
            "Ctrl" | "Control" => modifiers |= Modifiers::CONTROL,
            "Shift" => modifiers |= Modifiers::SHIFT,
            "Alt" => modifiers |= Modifiers::ALT,
            "Meta" | "Win" => modifiers |= Modifiers::META,
            key => {
                if key_code.is_some() {
                    return Err("å¿«æ·é”®åªèƒ½åŒ…å«ä¸€ä¸ªä¸»é”®".to_string());
                }
                key_code = Some(string_to_code(key)?);
            }
        }
    }

    match key_code {
        Some(code) => Ok(Shortcut::new(if modifiers.is_empty() { None } else { Some(modifiers) }, code)),
        None => Err("å¿«æ·é”®å¿…é¡»åŒ…å«ä¸€ä¸ªä¸»é”®".to_string())
    }
}


