static LAST_TRIGGER: Mutex<Option<Instant>> = Mutex::new(None);
static LAST_CLICK_POS: Mutex<(i32, i32)> = Mutex::new((0, 0));

#[derive(Serialize, Deserialize)]
struct TranslateRequest {
